/**
 * Hi list component
 * @author The Hi Framework Team
 */
var hiListDirective =  {};
hiListDirective.restrict = 'E';
hiListDirective.scope = {};
hiListDirective.template = "<h1>Hello World</h1>";
hiListDirective.compile = function(element, attributes) {

    element.css("border", "1px solid #cccccc");

    //linkFunction is linked with each element with scope to get the element specific data.

    var linkFunction = function($scope, element, attributes) {

        element.html("Student: <b>"+$scope.student.name +"</b> , Roll No: <b>"+$scope.student.rollno+"</b><br/>");
        element.css("background-color", "#ff00ff");


    }


    return linkFunction;
}



Hi.$ui.js.component("list",function(){

    return hiListDirective;

});